import { useState } from "react";

import { useNavigate, Link } from "react-router-dom";
import { TextTitle } from "../Layout/Layoutcss";
import Main from "../Layout/main";
import SaveToLocal from "./SaveToLocal";
import Validate from "./Validation";


const Login =(props)=> {
    const navigate = useNavigate();


    const [user, setUser] = useState({
        userName : '',
        password : ''
    });

    const [err, setErr] = useState("");
    const [showError, setShowError] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    const handleChange = (e) => {
        e.preventDefault();
        setUser(user => ({...user, [e.target.name] : e.target.value }));

    }

    // ここでローカルに保存
    //　save on local storage
    function handleSubmit(e) {
        e.preventDefault();

        // if user name and password is same with local storage one
        if(user.userName === localStorage.getItem('fullName') && user.password === localStorage.getItem('password')){
            SaveToLocal('userLoggedIn', true);
            navigate("/")
        }
        else {
            console.log("Bad Credentials");
            setShowError = true;
        }
    }

    return (
        <Main>
            <form onSubmit={handleSubmit} className={"text-center"}>
            <TextTitle>Sing in</TextTitle>
                <input className="border-b-2 border-slate-800 my-3" type="text" name="userName" onChange = {handleChange} placeholder="Name"  />
                <br />
                <input className="border-b-2 border-slate-800 my-3" type={showPassword ? "text" : "password"} name="password" onChange= {handleChange} placeholder="Password" />
                <br />
                {
                    showError &&
                    <p>invalid value</p>
                }
                <input type={"checkbox"} onChange = {()=> setShowPassword(!showPassword)}></input>
                <small> show password</small>
                <br />
                <button type="submit" className="bg-purple-500 rounded-md shadow-md hover:shadow-none hover:bg-purple-800 text-white py-2 px-5 font-bold" >Submit</button>
                <br />
                <br />
                <p >Don't have an account?<Link className="pl-3 font-bold" to={"/register"}> Sign Up </Link></p>

            </form>
        </Main>
    );
}
export default Login
