import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { TextTitle } from "../Layout/Layoutcss";
import Main from "../Layout/main";
import validate from "./Validation";
import useForm from "./useForm";

// To save in local storage
import SaveToLocal from "./SaveToLocal";

const Register =(props)=> {
    const navigate = useNavigate();
    const {
        values,
        errors,
      } = useForm(login, validate);

    const [user, setUser] = useState({
        userEmail : '',
        password : '',
        fullName : ''
    });

    const date = new Date () ;
    const year = date.getFullYear() ;
    const month = date.getMonth() ;
    const newMonth = [ "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec" ][month] ;
    const day = date.getDate() ;
    const dayOfWeek = date.getDay() ;
    const newWeek = [ "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat" ][dayOfWeek] ;

    const handleChange = (e) => {
        e.preventDefault();
        setUser(user => ({...user, [e.target.name] : e.target.value }));
    }

    function login() {
      console.log('No errors, submit callback called!');
    }

    function handleSubmit(e) {
        e.preventDefault();
        console.log(user);
        SaveToLocal("fullName", user.fullName);
        SaveToLocal("userEmail", user.userEmail);
        SaveToLocal("password", user.password);
        SaveToLocal("year", year);
        SaveToLocal("month", newMonth);
        SaveToLocal("day", day);
        SaveToLocal("week", newWeek);
        navigate('/login');
    }

    return (
        <Main>
            <form onSubmit={handleSubmit} className={" text-center"}>
                <TextTitle>Sing up</TextTitle>
                {/* input user fullName */}
                <input className="border-b-2 border-slate-800 my-3" type="text" name="fullName" onChange = {handleChange} placeholder="Name" required />
                <br />
                {/* input user email */}
                <input className={`input ${errors.email && "border-b-2 border-slate-800 my-3"}`} type="text" name="userEmail" onChange = {handleChange} placeholder="Email" required />
                {errors.email && (
                    <p className="help is-danger">{errors.email}</p>
                  )}
                <br />
                {/* input passWord */}
                <input className="border-b-2 border-slate-800 my-3" type="password" name="password" onChange= {handleChange} placeholder="Password" required />
                <br />
                <button type="submit" className="bg-purple-500 rounded-md shadow-md hover:shadow-none hover:bg-purple-800 text-white py-2 px-5 font-bold" >Submit</button>
            </form>
        </Main>
    );
}

export default Register
