import { useState } from "react";
import { Link } from "react-router-dom";

const Navigation = (props) => {

    const [isUserLoggedIn, setUserLoggedIn] = useState(props.userLoggedIn);

    return(
        <div className="flex justify-start items-center py-3 px-10 gap-10 bg-[#303030] text-white font-bold">
            <Link to={"/"}>Home {props.userLoggedIn}</Link>
            <Link to={"/forum"}>Forum</Link>
            <Link to={"/"} className="right-">Contact</Link>
            {
                isUserLoggedIn ?
                <Link className="" to={"/"} onClick={() =>{
                    localStorage.clear()}}> Logout</Link>
                : <><Link className="text-rose-700" to={"/register"}>SING UP</Link>
                <Link className="text-white" to={"/login"}>SING IN</Link></>
            }
        </div>
    );
}

export default Navigation;
